    var digits = Array(String(n)).map { Int(String($0))! }
    var i = digits.count - 2
    while i >= 0 && digits[i] >= digits[i + 1] {
        i -= 1
    }
    if i < 0 {
        return false
    }
    var j = digits.count - 1
    while j >= 0 && digits[j] <= digits[i] {
        j -= 1
    }
    digits.swapAt(i, j)
    digits = digits.prefix(i + 1) + digits.suffix(digits.count - i - 1).sorted()
    let result = digits.reduce(0) { $0 * 10 + $1 }
    return result > n ? result : false
}

