struct PriorityQueue<T: Comparable> {
    private var heap: [T] = []
    private let ascending: Bool
    init(ascending: Bool = true) {
        self.ascending = ascending
    }
    mutating func offer(_ element: T) {
        heap.append(element)
        if ascending {
            heap.sort(by: <)
        } else {
            heap.sort(by: >)
        }
    }
    mutating func poll() -> T? {
        if let element = heap.first {
            heap.removeFirst()
            return element
        }
        return nil
    }
}

