    var placement = 1
    var sortedNums = nums
    while maxDigit / placement > 0 {
        var buckets: [[Int]] = Array(repeating: [], count: 10)
        for number in sortedNums {
            let index = (number / placement) % 10
            buckets[index].append(number)
        }
        sortedNums = buckets.flatMap { $0 }
        placement *= 10
    }
    return sortedNums
}

