    let g = Double(g) / 255.0
    let b = Double(b) / 255.0
    let mx = max(r, g, b)
    let mn = min(r, g, b)
    let df = mx - mn
    var h: Double = 0
    var s: Double = 0
    var v: Double = mx * 100
    if mx == mn {
        h = 0
    } else if mx == r {
        h = (60 * ((g - b) / df) + 360).truncatingRemainder(dividingBy: 360)
    } else if mx == g {
        h = (60 * ((b - r) / df) + 120).truncatingRemainder(dividingBy: 360)
    } else if mx == b {
        h = (60 * ((r - g) / df) + 240).truncatingRemainder(dividingBy: 360)
    }
    if mx != 0 {
        s = (df / mx) * 100
    }
    return [h, s, v]
}

