func siftDown(_ heap: inout [Int], _ i: Int, _ size: Int) {
    var parent = i
    var leftChild = 2 * i + 1
    var rightChild = 2 * i + 2
    if leftChild < size && heap[leftChild] < heap[parent] {
        parent = leftChild
    }
    if rightChild < size && heap[rightChild] < heap[parent] {
        parent = rightChild
    }
    if parent != i {
        heap.swapAt(i, parent)
        siftDown(&heap, parent, size)
    }
}

