func heapify(_ arr: inout [Int]) {
    let start = arr.count / 2 - 1
    var i = start
    while i >= 0 {
        shiftDown(&arr, i, arr.count - 1)
        i -= 1
    }
}
func shiftDown(_ arr: inout [Int], _ start: Int, _ end: Int) {
    var root = start
    while (root * 2 + 1) <= end {
        let child = root * 2 + 1
        let rightChild = child + 1
        var swap = root
        if arr[swap] < arr[child] {
            swap = child
        }
        if rightChild <= end && arr[swap] < arr[rightChild] {
            swap = rightChild
        }
        if swap == root {
            return
        } else {
            arr.swapAt(root, swap)
            root = swap
        }
    }
}

