    # Find the smallest divisor of $n,
    # that is greater than $n.
    # Note: this means that if the smallest divisor of $n
    # is 1, the smallest divisor of $n + 1 is 2, ...
    # So there is no need to check if $n is prime.

    my $i = 2;

    while ($i <= $n) {
        if ($n % $i == 0) {
            return $i;
        }
        $i = $i + 1;
    }

    # If the loop exits, then $n is prime.
    # Return $n.
    return $n;
}

